import { Component } from '@angular/core';

interface Formation {
  title: string;
  regions: Region[];
}

interface Region {
  name: string;
  total: string;
  establishments: Establishment[];
}

interface Establishment {
  name: string;
  classes: string;
  progression: number;
  percentage: string;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent {
  selectedEtablissement: any;
  selectedCommune: any;

  formations: Formation[] = [
    {
      title: "1ᵉ formation des filles - 1ᵉʳ cycle",
      regions: [
        {
          name: "COMÈ",
          total: "45/45",
          establishments: [
            { name: "CEG 2 COMÈ", classes: "12/12", progression: 100, percentage: "100%" },
            { name: "CEG 4 COMÈ", classes: "11/11", progression: 100, percentage: "100%" },
            { name: "CEG AKODEHA", classes: "22/22", progression: 100, percentage: "100%" }
          ]
        },
        {
          name: "BANTÈ",
          total: "10/16",
          establishments: [
            { name: "CEG 1 BANTÈ", classes: "2/6", progression: 20, percentage: "20%" },
            { name: "CEG AKPASSI", classes: "2/4", progression: 50, percentage: "50%" },
            { name: "CEG ANOUDE", classes: "6/6", progression: 100, percentage: "100%" }
          ]
        },
        {
          name: "HOUÉYOGBÉ",
          total: "20/51",
          establishments: [
            { name: "CEG HOUÉYOGBÉ", classes: "20/24", progression: 45, percentage: "45%" },
            { name: "CEG 1 DOUTOU", classes: "4/19", progression: 21, percentage: "21%" },
            { name: "CEG AHOULOUMÈ", classes: "3/8", progression: 35, percentage: "35%" }
          ]
        },
        {
          name: "SAVALOU",
          total: "44/44",
          establishments: [
            { name: "CEG 1 SAVALOU", classes: "24/24", progression: 100, percentage: "100%" },
            { name: "CEG 2 SAVALOU", classes: "11/11", progression: 100, percentage: "100%" },
            { name: "CEG TCHOGODO", classes: "9/9", progression: 100, percentage: "100%" }
          ]
        }
      ]
    },
    {
      title: "1ᵉ formation des filles - 2ᵉ cycle",
      regions: [
        {
          name: "COMÈ",
          total: "51/51",
          establishments: [
            { name: "CEG 2 COMÈ", classes: "16/16", progression: 100, percentage: "100%" },
            { name: "CEG 4 COMÈ", classes: "16/16", progression: 100, percentage: "100%" },
            { name: "CEG AKODEHA", classes: "19/19", progression: 100, percentage: "100%" }
          ]
        },
        {
          name: "BANTÈ",
          total: "45/45",
          establishments: [
            { name: "CEG 1 BANTÈ", classes: "21/21", progression: 100, percentage: "100%" },
            { name: "CEG AKPASSI", classes: "13/13", progression: 100, percentage: "100%" },
            { name: "CEG ANOUDE", classes: "11/11", progression: 100, percentage: "100%" }
          ]
        },
        {
          name: "HOUÉYOGBÉ",
          total: "51/51",
          establishments: [
            { name: "CEG HOUÉYOGBÉ", classes: "24/24", progression: 100, percentage: "100%" },
            { name: "CEG 1 DOUTOU", classes: "19/19", progression: 100, percentage: "100%" },
            { name: "CEG AHOULOUMÈ", classes: "8/8", progression: 100, percentage: "100%" }
          ]
        },
        {
          name: "SAVALOU",
          total: "44/44",
          establishments: [
            { name: "CEG 1 SAVALOU", classes: "24/24", progression: 100, percentage: "100%" },
            { name: "CEG 2 SAVALOU", classes: "11/11", progression: 100, percentage: "100%" },
            { name: "CEG TCHOGODO", classes: "9/9", progression: 100, percentage: "100%" }
          ]
        }
      ]
    }
  ];

  getProgressColor(percentage: number): string {
    if (percentage >= 100) return 'bg-green-500';
    if (percentage >= 50) return 'bg-yellow-500';
    return 'bg-red-500';
  }
}
<div class="p-4">
  <!-- Filtres -->
  <div class="flex gap-4 mb-6">
    <p-dropdown 
      [options]="[{label: 'Tous les établissements', value: 'all'}]" 
      [(ngModel)]="selectedEtablissement"
      placeholder="Tous les établissements"
      styleClass="w-72">
    </p-dropdown>

    <p-dropdown 
      [options]="[{label: 'Toutes les communes', value: 'all'}]" 
      [(ngModel)]="selectedCommune"
      placeholder="Toutes les communes"
      styleClass="w-72">
    </p-dropdown>
  </div>

  <!-- Section Taux de réalisation -->
  <div class="bg-white rounded-lg shadow-sm p-6">
    <div class="flex justify-between items-center mb-6">
      <h2 class="text-blue-700 text-xl font-semibold">TAUX DE RÉALISATION DES FORMATIONS</h2>
      <i class="pi pi-chevron-down text-gray-600 cursor-pointer"></i>
    </div>

    <!-- Formation Title -->
    <div *ngFor="let formation of formations; let last = last" class="formation-section">
      <h3 class="text-blue-600 mb-4">{{ formation.title }}</h3>

      <!-- Grille des régions -->
      <div class="grid grid-cols-2 gap-6">
        <div *ngFor="let region of formation.regions" class="bg-white rounded-lg shadow">
          <div class="bg-[#4070B8] text-white p-3 rounded-t-lg flex justify-between items-center">
            <span class="font-semibold">{{ region.name }}</span>
            <span>{{ region.total }}</span>
            <span class="text-white">{{ region.establishments[0].percentage }}</span>
          </div>
          <div class="p-4">
            <table class="w-full">
              <thead>
                <tr class="text-left text-gray-600">
                  <th class="pb-3 pr-8">Établissement</th>
                  <th class="pb-3 px-8">Nb classes</th>
                  <th class="pb-3 px-8">Progression</th>
                  <th class="pb-3 pl-8">Pourcentage</th>
                </tr>
              </thead>
              <tbody>
                <tr *ngFor="let establishment of region.establishments" 
                    class="border-b last:border-b-0">
                  <td class="py-2 pr-8">{{ establishment.name }}</td>
                  <td class="py-2 px-8">{{ establishment.classes }}</td>
                  <td class="py-2 px-8">
                    <div class="bg-gray-200 rounded-full h-2">
                      <div [class]="getProgressColor(establishment.progression) + ' h-2 rounded-full'"
                           [style.width]="establishment.progression + '%'"></div>
                    </div>
                  </td>
                  <td class="py-2 pl-8">{{ establishment.percentage }}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
      <!-- Séparateur sauf pour le dernier élément -->
      <div *ngIf="!last" class="border-b border-gray-200 my-8"></div>
    </div>
  </div>
</div>
