<div class="p-4">
  <!-- Filtres -->
  <div class="flex gap-4 mb-6">
    <p-dropdown 
      [options]="[{label: 'Tous les établissements', value: 'all'}]" 
      [(ngModel)]="selectedEtablissement"
      placeholder="Tous les établissements"
      styleClass="w-72">
    </p-dropdown>

    <p-dropdown 
      [options]="[{label: 'Toutes les communes', value: 'all'}]" 
      [(ngModel)]="selectedCommune"
      placeholder="Toutes les communes"
      styleClass="w-72">
    </p-dropdown>
  </div>

  <!-- Section Taux de réalisation -->
  <div class="bg-white rounded-lg shadow-sm p-6">
    <div class="flex justify-between items-center mb-6">
      <h2 class="text-blue-700 text-xl font-semibold">TAUX DE RÉALISATION DES FORMATIONS</h2>
      <i class="pi pi-chevron-{{ isExpanded ? 'up' : 'down' }} text-gray-600 cursor-pointer hover:text-gray-800 transition-colors"
         (click)="toggleExpand()"></i>
    </div>

    <!-- Formation Title -->
    <div class="space-y-8" [ngClass]="{'hidden': !isExpanded}">
      <div *ngFor="let formation of formations; let last = last" class="formation-section">
        <h3 class="text-blue-600 mb-4">{{ formation.title }}</h3>

        <!-- Grille des régions -->
        <div class="grid grid-cols-2 gap-6">
          <div *ngFor="let region of formation.regions" class="bg-white rounded-lg shadow shadow-md">
            <div class="bg-[#4070B8] text-white p-3 rounded-t-lg flex justify-between items-center">
              <span class="font-semibold">{{ region.name }}</span>
              <span>{{ region.total }}</span>
              <span class="text-white">{{ region.establishments[0].percentage }}</span>
            </div>
            <div class="p-4">
              <table class="w-full">
                <thead>
                  <tr class="text-left text-gray-600 text-sm bg-gray-50">
                    <th class="pb-2 pr-4 font-bold px-2 rounded-tl">Établissement</th>
                    <th class="pb-2 px-4 font-bold px-2">Nb classes</th>
                    <th class="pb-2 px-4 font-bold px-2">Progression</th>
                    <th class="pb-2 pl-4 font-bold px-2 rounded-tr">Pourcentage</th>
                  </tr>
                </thead>
                <tbody>
                  <tr *ngFor="let establishment of region.establishments" 
                      class="border-b last:border-b-0 hover:bg-gray-50">
                    <td class="py-2 pr-4 text-sm px-2">{{ establishment.name }}</td>
                    <td class="py-2 px-4 text-sm px-2">{{ establishment.classes }}</td>
                    <td class="py-2 px-4">
                      <div class="bg-gray-200 rounded-full h-1.5 w-24">
                        <div [class]="getProgressColor(establishment.progression) + ' h-1.5 rounded-full'"
                             [style.width]="establishment.progression + '%'"></div>
                      </div>
                    </td>
                    <td class="py-2 pl-4 text-sm px-2">{{ establishment.percentage }}</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
        <!-- Séparateur sauf pour le dernier élément -->
        <div *ngIf="!last" class="border-b border-gray-200 my-8"></div>
      </div>
    </div>
  </div>
  <!-- Section Kits Restants -->
  <div class="bg-white rounded-lg shadow-sm p-6 mt-6">
    <div class="flex justify-between items-center mb-6">
      <h2 class="text-blue-700 text-xl font-semibold">NB DE KITS RESTANTS</h2>
      <i class="pi pi-chevron-{{ isKitsExpanded ? 'up' : 'down' }} text-gray-600 cursor-pointer hover:text-gray-800 transition-colors"
         (click)="toggleKitsExpand()"></i>
    </div>

    <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4" [ngClass]="{'hidden': !isKitsExpanded}">
      <div *ngFor="let region of regionKits" 
           [ngClass]="{'bg-red-500': region.isLow, 'bg-[#4070B8]': !region.isLow}"
           class="rounded-lg shadow-md">
        <div class="p-3 text-white flex justify-between items-center">
          <span class="font-semibold">{{region.name}}</span>
          <div class="flex items-center">
            <span class="font-semibold">{{region.total}}</span>
            <i *ngIf="region.isLow" class="pi pi-exclamation-triangle ml-2"></i>
          </div>
        </div>

        <div class="bg-white p-4 rounded-b-lg">
          <table class="w-full">
            <thead>
              <tr class="text-left text-gray-600 text-sm bg-gray-50">
                <th class="pb-2 font-bold px-2 rounded-tl">Type de kits</th>
                <th class="pb-2 text-right font-bold px-2 rounded-tr">Nb restant</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let kit of region.kits" class="border-b last:border-b-0 hover:bg-gray-50">
                <td class="py-2 text-sm px-2">{{kit.name}}</td>
                <td class="py-2 text-sm text-right px-2">{{kit.remaining}}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
  <!-- Section Élections -->
  <div class="bg-white rounded-lg shadow-sm p-6 mt-6">
    <div class="flex justify-between items-center mb-6">
      <h2 class="text-blue-700 text-xl font-semibold">TAUX DE RÉALISATION DES ÉLECTIONS</h2>
      <i class="pi pi-chevron-{{ isElectionsExpanded ? 'up' : 'down' }} text-gray-600 cursor-pointer hover:text-gray-800 transition-colors"
         (click)="toggleElectionsExpand()"></i>
    </div>

    <div class="space-y-4" [ngClass]="{'hidden': !isElectionsExpanded}">
      <!-- Première ligne -->
      <div class="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div *ngFor="let region of elections.slice(0, 3)" 
             class="rounded-xl">
          <div class="bg-[#4070B8] text-white p-3 flex justify-between items-center rounded-xl mb-1">
            <span class="font-semibold">{{region.name}}</span>
            <div class="flex items-center gap-4">
              <span>{{region.total}}</span>
              <div class="bg-white/30 rounded-full h-1.5 w-24 relative">
                <div [class]="getProgressColor(region.progression) + ' h-1.5 rounded-full absolute top-0 left-0'"
                     [style.width]="region.progression + '%'"></div>
              </div>
              <span>{{region.percentage}}</span>
            </div>
          </div>

          <div class="bg-white p-6 rounded-xl shadow-lg">
            <div *ngFor="let establishment of region.establishments" class="mb-5 last:mb-0">
              <div class="flex items-center gap-4">
                <span class="text-sm text-gray-700 min-w-[120px]">{{establishment.name}}</span>
                <div class="bg-gray-100 rounded-full h-1.5 flex-1 relative">
                  <div [class]="getProgressColor(establishment.progression) + ' h-1.5 rounded-full absolute top-0 left-0'"
                       [style.width]="establishment.progression + '%'"></div>
                </div>
                <span class="text-sm text-gray-700 min-w-[45px] text-right">{{establishment.completed}}</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <!-- Deuxième ligne (Savalou) -->
      <div class="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div *ngFor="let region of elections.slice(3, 4)" 
             class="rounded-xl">
          <div class="bg-[#4070B8] text-white p-3 flex justify-between items-center rounded-xl mb-1">
            <span class="font-semibold">{{region.name}}</span>
            <div class="flex items-center gap-4">
              <span>{{region.total}}</span>
              <div class="bg-white/30 rounded-full h-1.5 w-24 relative">
                <div [class]="getProgressColor(region.progression) + ' h-1.5 rounded-full absolute top-0 left-0'"
                     [style.width]="region.progression + '%'"></div>
              </div>
              <span>{{region.percentage}}</span>
            </div>
          </div>

          <div class="bg-white p-6 rounded-xl shadow-lg">
            <div *ngFor="let establishment of region.establishments" class="mb-5 last:mb-0">
              <div class="flex items-center gap-4">
                <span class="text-sm text-gray-700 min-w-[120px]">{{establishment.name}}</span>
                <div class="bg-gray-100 rounded-full h-1.5 flex-1 relative">
                  <div [class]="getProgressColor(establishment.progression) + ' h-1.5 rounded-full absolute top-0 left-0'"
                       [style.width]="establishment.progression + '%'"></div>
                </div>
                <span class="text-sm text-gray-700 min-w-[45px] text-right">{{establishment.completed}}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
  <div class="p-4">
    <!-- Filtres -->
    <div class="flex gap-4 mb-6">
      <p-dropdown 
        [options]="[{label: 'Tous les établissements', value: 'all'}]" 
        [(ngModel)]="selectedEtablissement"
        placeholder="Tous les établissements"
        styleClass="w-72">
      </p-dropdown>

      <p-dropdown 
        [options]="[{label: 'Toutes les communes', value: 'all'}]" 
        [(ngModel)]="selectedCommune"
        placeholder="Toutes les communes"
        styleClass="w-72">
      </p-dropdown>
    </div>

    <!-- Section Taux de réalisation -->
    <div class="bg-white rounded-lg shadow-sm p-6">
      <div class="flex justify-between items-center mb-6">
        <h2 class="text-blue-700 text-xl font-semibold">TAUX DE RÉALISATION DES FORMATIONS</h2>
        <i class="pi pi-chevron-{{ isExpanded ? 'up' : 'down' }} text-gray-600 cursor-pointer hover:text-gray-800 transition-colors"
           (click)="toggleExpand()"></i>
      </div>

      <!-- Formation Title -->
      <div class="space-y-8" [ngClass]="{'hidden': !isExpanded}">
        <div *ngFor="let formation of formations; let last = last" class="formation-section">
          <h3 class="text-blue-600 mb-4">{{ formation.title }}</h3>

          <!-- Grille des régions -->
          <div class="grid grid-cols-2 gap-6">
            <div *ngFor="let region of formation.regions" class="bg-white rounded-lg shadow shadow-md">
              <div class="bg-[#4070B8] text-white p-3 rounded-t-lg flex justify-between items-center">
                <span class="font-semibold">{{ region.name }}</span>
                <span>{{ region.total }}</span>
                <span class="text-white">{{ region.establishments[0].percentage }}</span>
              </div>
              <div class="p-4">
                <table class="w-full">
                  <thead>
                    <tr class="text-left text-gray-600 text-sm bg-gray-50">
                      <th class="pb-2 pr-4 font-bold px-2 rounded-tl">Établissement</th>
                      <th class="pb-2 px-4 font-bold px-2">Nb classes</th>
                      <th class="pb-2 px-4 font-bold px-2">Progression</th>
                      <th class="pb-2 pl-4 font-bold px-2 rounded-tr">Pourcentage</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr *ngFor="let establishment of region.establishments" 
                        class="border-b last:border-b-0 hover:bg-gray-50">
                      <td class="py-2 pr-4 text-sm px-2">{{ establishment.name }}</td>
                      <td class="py-2 px-4 text-sm px-2">{{ establishment.classes }}</td>
                      <td class="py-2 px-4">
                        <div class="bg-gray-200 rounded-full h-1.5 w-24">
                          <div [class]="getProgressColor(establishment.progression) + ' h-1.5 rounded-full'"
                               [style.width]="establishment.progression + '%'"></div>
                        </div>
                      </td>
                      <td class="py-2 pl-4 text-sm px-2">{{ establishment.percentage }}</td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>
          <!-- Séparateur sauf pour le dernier élément -->
          <div *ngIf="!last" class="border-b border-gray-200 my-8"></div>
        </div>
      </div>
    </div>

    <!-- Section Kits Restants -->
    <div class="bg-white rounded-lg shadow-sm p-6 mt-6">
      <div class="flex justify-between items-center mb-6">
        <h2 class="text-blue-700 text-xl font-semibold">NB DE KITS RESTANTS</h2>
        <i class="pi pi-chevron-{{ isKitsExpanded ? 'up' : 'down' }} text-gray-600 cursor-pointer hover:text-gray-800 transition-colors"
           (click)="toggleKitsExpand()"></i>
      </div>

      <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4" [ngClass]="{'hidden': !isKitsExpanded}">
        <div *ngFor="let region of regionKits" 
             [ngClass]="{'bg-red-500': region.isLow, 'bg-[#4070B8]': !region.isLow}"
             class="rounded-lg shadow-md">
          <div class="p-3 text-white flex justify-between items-center">
            <span class="font-semibold">{{region.name}}</span>
            <div class="flex items-center">
              <span class="font-semibold">{{region.total}}</span>
              <i *ngIf="region.isLow" class="pi pi-exclamation-triangle ml-2"></i>
            </div>
          </div>

          <div class="bg-white p-4 rounded-b-lg">
            <table class="w-full">
              <thead>
                <tr class="text-left text-gray-600 text-sm bg-gray-50">
                  <th class="pb-2 font-bold px-2 rounded-tl">Type de kits</th>
                  <th class="pb-2 text-right font-bold px-2 rounded-tr">Nb restant</th>
                </tr>
              </thead>
              <tbody>
                <tr *ngFor="let kit of region.kits" class="border-b last:border-b-0 hover:bg-gray-50">
                  <td class="py-2 text-sm px-2">{{kit.name}}</td>
                  <td class="py-2 text-sm text-right px-2">{{kit.remaining}}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>

    <!-- Section Élections -->
    <div class="bg-white rounded-lg shadow-sm p-6 mt-6">
      <div class="flex justify-between items-center mb-6">
        <h2 class="text-blue-700 text-xl font-semibold">TAUX DE RÉALISATION DES ÉLECTIONS</h2>
        <i class="pi pi-chevron-{{ isElectionsExpanded ? 'up' : 'down' }} text-gray-600 cursor-pointer hover:text-gray-800 transition-colors"
           (click)="toggleElectionsExpand()"></i>
      </div>

      <div class="space-y-4" [ngClass]="{'hidden': !isElectionsExpanded}">
        <!-- Première ligne -->
        <div class="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div *ngFor="let region of elections.slice(0, 3)" 
               class="rounded-xl">
            <div class="bg-[#4070B8] text-white p-3 flex justify-between items-center rounded-xl mb-1">
              <span class="font-semibold">{{region.name}}</span>
              <div class="flex items-center gap-4">
                <span>{{region.total}}</span>
                <div class="bg-white/30 rounded-full h-1.5 w-24 relative">
                  <div [class]="getProgressColor(region.progression) + ' h-1.5 rounded-full absolute top-0 left-0'"
                       [style.width]="region.progression + '%'"></div>
                </div>
                <span>{{region.percentage}}</span>
              </div>
            </div>

            <div class="bg-white p-6 rounded-xl shadow-lg">
              <div *ngFor="let establishment of region.establishments" class="mb-5 last:mb-0">
                <div class="flex items-center gap-4">
                  <span class="text-sm text-gray-700 min-w-[120px]">{{establishment.name}}</span>
                  <div class="bg-gray-100 rounded-full h-1.5 flex-1 relative">
                    <div [class]="getProgressColor(establishment.progression) + ' h-1.5 rounded-full absolute top-0 left-0'"
                         [style.width]="establishment.progression + '%'"></div>
                  </div>
                  <span class="text-sm text-gray-700 min-w-[45px] text-right">{{establishment.completed}}</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        <!-- Deuxième ligne (Savalou) -->
        <div class="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div *ngFor="let region of elections.slice(3, 4)" 
               class="rounded-xl">
            <div class="bg-[#4070B8] text-white p-3 flex justify-between items-center rounded-xl mb-1">
              <span class="font-semibold">{{region.name}}</span>
              <div class="flex items-center gap-4">
                <span>{{region.total}}</span>
                <div class="bg-white/30 rounded-full h-1.5 w-24 relative">
                  <div [class]="getProgressColor(region.progression) + ' h-1.5 rounded-full absolute top-0 left-0'"
                       [style.width]="region.progression + '%'"></div>
                </div>
                <span>{{region.percentage}}</span>
              </div>
            </div>

            <div class="bg-white p-6 rounded-xl shadow-lg">
              <div *ngFor="let establishment of region.establishments" class="mb-5 last:mb-0">
                <div class="flex items-center gap-4">
                  <span class="text-sm text-gray-700 min-w-[120px]">{{establishment.name}}</span>
                  <div class="bg-gray-100 rounded-full h-1.5 flex-1 relative">
                    <div [class]="getProgressColor(establishment.progression) + ' h-1.5 rounded-full absolute top-0 left-0'"
                         [style.width]="establishment.progression + '%'"></div>
                  </div>
                  <span class="text-sm text-gray-700 min-w-[45px] text-right">{{establishment.completed}}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>

    <!-- Section Activités Récréatives -->
    <div class="bg-white rounded-lg shadow-sm p-6 mt-6">
      <div class="flex justify-between items-center mb-6">
        <h2 class="text-blue-700 text-xl font-semibold">NB D'INSCRITS POUR CHAQUE ACTIVITÉ RÉCRÉATIVE (SLAM, ART PLASTIQUE, THÉÂTRE, CHAMPIONNE GHM)</h2>
        <i class="pi pi-chevron-{{ isActivitiesExpanded ? 'up' : 'down' }} text-gray-600 cursor-pointer hover:text-gray-800 transition-colors"
           (click)="toggleActivitiesExpand()"></i>
      </div>

      <div class="space-y-8" [ngClass]="{'hidden': !isActivitiesExpanded}">
        <div *ngFor="let activity of activities">
          <h3 class="text-lg font-semibold mb-4">{{activity.name}}</h3>
          <div class="grid grid-cols-1 md:grid-cols-4 gap-6">
            <div *ngFor="let region of activity.regions" 
                 class="rounded-xl">
              <div class="bg-[#4070B8] text-white p-3 flex justify-between items-center rounded-xl mb-1">
                <span class="font-semibold">{{region.name}}</span>
                <span>{{region.total}}</span>
              </div>

              <div class="bg-white p-6 rounded-xl shadow-lg">
                <div class="bg-gray-50 flex justify-between items-center p-2 rounded-lg mb-4">
                  <span class="text-sm font-bold text-gray-600">Établissement</span>
                  <span class="text-sm font-bold text-gray-600">Nb d'inscrits</span>
                </div>
                <div *ngFor="let establishment of region.establishments" class="mb-4 last:mb-0">
                  <div class="flex justify-between items-center">
                    <span class="text-sm text-gray-700">{{establishment.name}}</span>
                    <span class="text-sm text-gray-700">{{establishment.inscriptions}}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

</div>
