import { Component } from '@angular/core';
import { SidebarService } from '../services/sidebar.service';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.scss']
})
export class SidebarComponent {
  constructor(private sidebarService: SidebarService) {}

  get isSidebarCollapsed() {
    return this.sidebarService.getIsCollapsed();
  }

  toggleSidebar() {
    this.sidebarService.toggleSidebar();
  }

  menuItems = [
    { label: 'Préparation de l\'édition', icon: 'pi pi-file', expanded: true, items: [
      { label: 'Identification des établissements', route: '/identification' },
      { label: 'Composition des équipes', route: '/composition' },
      { label: 'Recrutement équipe GHM', route: '/recrutement' }
    ]},
    { label: 'Tableaux de bord', icon: 'pi pi-chart-bar', route: '/dashboard' },
    { label: 'Gestion de projet', icon: 'pi pi-briefcase', route: '/gestion' },
    { label: 'Suivi-évaluation', icon: 'pi pi-chart-line', route: '/suivi' },
    { label: 'Gestion des ressources', icon: 'pi pi-users', route: '/ressources' },
    { label: 'Bibliothèque', icon: 'pi pi-book', route: '/bibliotheque' },
    { label: 'Paramètrages', icon: 'pi pi-cog', route: '/parametres' }
  ];

  isPreparationCollapsed = false;

  togglePreparation() {
    this.isPreparationCollapsed = !this.isPreparationCollapsed;
  }
}
<div class="sidebar bg-white h-screen fixed left-0 top-0" [ngClass]="{'collapsed': isSidebarCollapsed}">
  <!-- En-tête du sidebar -->
  <div class="logo-container">
    <img src="assets/images/logos.png" alt="Happy Meet Logo">
    <div class="toggle-button" (click)="toggleSidebar()">
      <i class="pi" [ngClass]="{'pi-angle-double-right': isSidebarCollapsed, 'pi-angle-double-left': !isSidebarCollapsed}"></i>
    </div>
  </div>

  <!-- Menu principal -->
  <div class="menu-container">
    <!-- Section Préparation -->
    <div class="menu-item p-3 flex items-center text-[#4070B8] cursor-pointer hover:bg-gray-100">
      <i class="pi pi-file menu-icon mr-3"></i>
      <span class="menu-label">Préparation de l'édition</span>
    </div>

    <!-- Tableau de bord -->
    <div class="menu-item p-3 flex items-center text-[#4070B8] cursor-pointer hover:bg-gray-100">
      <i class="pi pi-home menu-icon mr-3"></i>
      <span class="menu-label">Tableau de bord</span>
    </div>

    <!-- Autres menus -->
    <div class="menu-item p-3 flex items-center text-[#4070B8] cursor-pointer hover:bg-gray-100">
      <i class="pi pi-cog menu-icon mr-3"></i>
      <span class="menu-label">Paramètres</span>
    </div>
  </div>
</div>
