import { Component } from '@angular/core';
import { SidebarService } from '../services/sidebar.service';

interface Formation {
  title: string;
  regions: Region[];
}

interface Region {
  name: string;
  total: string;
  establishments: Establishment[];
}

interface Establishment {
  name: string;
  classes: string;
  progression: number;
  percentage: string;
}

interface KitType {
  name: string;
  remaining: number;
}

interface RegionKits {
  name: string;
  total: number;
  isLow: boolean;
  kits: KitType[];
}

interface ElectionEstablishment {
  name: string;
  completed: string;
  progression: number;
}

interface ElectionRegion {
  name: string;
  total: string;
  percentage: string;
  progression: number;
