}

interface ActivityEstablishment {
  name: string;
  inscriptions: string;
}

interface ActivityRegion {
  name: string;
  total: string;
  establishments: ActivityEstablishment[];
}

interface Activity {
  name: string;
  regions: ActivityRegion[];
}

interface TrainingRate {
  name: string;
  total: string;
  percentage: string;
  animators: Animator[];
}

interface Animator {
  name: string;
  count: string;
  percentage: string;
}

interface SupervisionRate {
  name: string;
  animators: SupervisionAnimator[];
}

interface SupervisionAnimator {
  name: string;
