import { Component } from '@angular/core';
import { MenuItem } from 'primeng/api';
import { SidebarService } from './services/sidebar.service';

@Component({
  selector: 'app-root',
  template: `
    <div class="flex min-h-screen bg-gray-50">
      <app-sidebar></app-sidebar>

      <div class="flex-1" [ngClass]="{'ml-64': !isSidebarCollapsed, 'ml-16': isSidebarCollapsed}">
        <header class="bg-white border-b border-opacity-10 h-8">
          <div class="px-4 h-full">
            <div class="flex justify-between items-center h-full">
              <!-- Bouton retour -->
              <button class="text-primary">
                <i class="pi pi-angle-left text-sm"></i>
              </button>

              <div class="flex items-center gap-6">
                <!-- Notification -->
                <div class="notification-wrapper relative">
                  <i class="pi pi-bell text-xs text-primary"></i>
                  <span class="notification-dot"></span>
                </div>

                <!-- Profile -->
                <p-menu #profileMenu [model]="profileItems" [popup]="true"></p-menu>
                <div class="profile-section" (click)="profileMenu.toggle($event)">
                  <div class="profile-info">
                    <span class="name">Sara AMAGEDJI</span>
                    <span class="role">Administrateur</span>
                  </div>
                  <img src="assets/images/avatar.png" alt="Profile" class="w-4 h-4 rounded-full">
                  <i class="pi pi-chevron-down text-[0.6rem] text-primary"></i>
                </div>
              </div>
            </div>
          </div>
        </header>

        <main class="p-6">
          <app-dashboard></app-dashboard>
        </main>
      </div>
    </div>
  `,
  styles: [`
    .text-primary {
      color: #4070B8;
    }

    header {
      border-color: rgba(0, 0, 0, 0.1);
    }

    .notification-dot {
      position: absolute;
      top: -1px;
      right: -1px;
      width: 3px;
      height: 3px;
      background: #ef4444;
      border-radius: 50%;
    }

    .profile-section {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      padding: 0.25rem 0.5rem;
      background: rgba(64, 112, 184, 0.04);
      border-radius: 20px;
      cursor: pointer;

      .profile-info {
        display: flex;
        flex-direction: column;
        align-items: flex-end;

        .name {
          font-size: 0.7rem;
          font-weight: 500;
          color: #4070B8;
          line-height: 1;
        }

        .role {
          font-size: 0.6rem;
          color: #6B7280;
          line-height: 1;
        }
      }
    }
  `]
})
export class AppComponent {
  constructor(private sidebarService: SidebarService) {}

  get isSidebarCollapsed() {
    return this.sidebarService.getIsCollapsed();
  }

  profileItems: MenuItem[] = [
    {
      label: 'Profile',
      icon: 'pi pi-user'
    },
    {
      label: 'Settings',
      icon: 'pi pi-cog'
    },
    {
      separator: true
    },
    {
      label: 'Sign out',
      icon: 'pi pi-sign-out'
    }
  ];
}
