import { Component } from '@angular/core';
import { MenuItem } from 'primeng/api';

@Component({
  selector: 'app-root',
  template: `
    <div class="flex min-h-screen bg-gray-50">
      <app-sidebar></app-sidebar>

      <div class="flex-1 ml-64">
        <header class="bg-white shadow-sm">
          <div class="px-6 py-4">
            <div class="flex justify-end items-center gap-2">
              <div class="notification-icon relative bg-gray-100 rounded-2xl p-2 cursor-pointer">
                <i class="pi pi-bell text-gray-600 text-xl"></i>
                <span class="notification-badge"></span>
              </div>
              <p-menu #profileMenu [model]="profileItems" [popup]="true"></p-menu>
              <div class="flex items-center gap-3 bg-gray-100 rounded-2xl p-2 cursor-pointer" (click)="profileMenu.toggle($event)">
                <div class="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                  <i class="pi pi-user text-blue-500"></i>
                </div>
                <div class="flex flex-col">
                  <span class="text-gray-700 font-medium">Sara AMAGEDJI</span>
                  <span class="text-gray-500 text-sm">Administrateur</span>
                </div>
                <i class="pi pi-chevron-down text-gray-600"></i>
              </div>
            </div>
          </div>
        </header>

        <main class="p-6">
          <app-dashboard></app-dashboard>
        </main>
      </div>
    </div>
  `,
  styles: [`
    .notification-badge {
      position: absolute;
      top: -2px;
      right: -2px;
      width: 8px;
      height: 8px;
      background-color: #FF0000;
      border-radius: 50%;
    }
  `]
})
export class AppComponent {
  title = 'formation-dashboard';

  profileItems: MenuItem[] = [
    {
      label: 'Mon Profil',
      icon: 'pi pi-user',
      command: () => {
        // Action pour voir le profil
      }
    },
    {
      label: 'Paramètres',
      icon: 'pi pi-cog',
      command: () => {
        // Action pour les paramètres
      }
    },
    {
      separator: true
    },
    {
      label: 'Déconnexion',
      icon: 'pi pi-power-off',
      command: () => {
        // Action pour la déconnexion
      }
    }
  ];
}
