import { Component, OnInit } from '@angular/core';

interface Formation {
  etablissement: string;
  nbClasses: string;
  progression: number;
  pourcentage: number;
}

interface RegionFormation {
  name: string;
  total: string;
  formations: Formation[];
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  selectedCycle: string = '1';
  selectedFormation: string = '1';
  regions: RegionFormation[] = [
    {
      name: 'COME',
      total: '45/45',
      formations: [
        { etablissement: 'CEG 2 COME', nbClasses: '12/12', progression: 100, pourcentage: 100 },
        { etablissement: 'CEG 4 COME', nbClasses: '11/11', progression: 100, pourcentage: 100 },
        { etablissement: 'CEG AKODEHA', nbClasses: '22/22', progression: 100, pourcentage: 100 }
      ]
    },
    {
      name: 'BANTÉ',
      total: '10/16',
      formations: [
        { etablissement: 'CEG 1 BANTÉ', nbClasses: '2/6', progression: 20, pourcentage: 20 },
        { etablissement: 'CEG AKPASSI', nbClasses: '2/4', progression: 50, pourcentage: 50 },
        { etablissement: 'CEG ANOUDE', nbClasses: '6/6', progression: 100, pourcentage: 100 }
      ]
    }
    // Add other regions here
  ];

  constructor() { }

  ngOnInit(): void { }

  getProgressBarClass(percentage: number): string {
    if (percentage >= 100) return 'bg-green-500';
    if (percentage >= 50) return 'bg-yellow-500';
    return 'bg-red-500';
  }
}
<div class="container mx-auto p-4">
  <div class="flex justify-between items-center mb-6">
    <div class="flex space-x-4">
      <p-dropdown [options]="[
        {label: 'Tous les établissements', value: 'all'},
        {label: 'CEG 1', value: 'ceg1'},
        {label: 'CEG 2', value: 'ceg2'}
      ]" placeholder="Tous les établissements"></p-dropdown>

      <p-dropdown [options]="[
        {label: 'Toutes les communes', value: 'all'},
        {label: 'COME', value: 'come'},
        {label: 'BANTÉ', value: 'bante'}
      ]" placeholder="Toutes les communes"></p-dropdown>
    </div>
  </div>

  <div class="mb-8">
    <h2 class="text-xl font-semibold text-blue-700 mb-4">TAUX DE RÉALISATION DES FORMATIONS</h2>

    <div class="grid grid-cols-1 md:grid-cols-2 gap-6">
      <div *ngFor="let region of regions" class="bg-white rounded-lg shadow-md p-4">
        <div class="flex justify-between items-center mb-4">
          <h3 class="text-lg font-semibold text-blue-600">{{region.name}}</h3>
          <span class="text-lg font-semibold">{{region.total}}</span>
        </div>

        <div class="space-y-4">
          <div *ngFor="let formation of region.formations" class="border-b pb-2">
            <div class="flex justify-between items-center mb-2">
              <span class="font-medium">{{formation.etablissement}}</span>
              <span>{{formation.nbClasses}}</span>
            </div>
            <div class="w-full bg-gray-200 rounded-full h-2.5">
              <div [class]="'h-2.5 rounded-full ' + getProgressBarClass(formation.pourcentage)"
                   [style.width]="formation.pourcentage + '%'"></div>
            </div>
            <div class="text-right text-sm mt-1">{{formation.pourcentage}}%</div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
